import React, { useState, useEffect, useRef, useCallback } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Typography from "@material-ui/core/Typography";
import { Button, Grid, IconButton, StepContent, TextField, Dialog, DialogTitle, DialogContent, DialogActions, Select, MenuItem, FormControl, InputLabel } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";
import SaveIcon from "@material-ui/icons/Save";
import EditIcon from "@material-ui/icons/Edit";
import LinkIcon from "@material-ui/icons/Link";
import api from "../../services/api";
import toastError from "../../errors/toastError";
import { AttachFile, DeleteOutline } from "@material-ui/icons";
import { head } from "lodash";
import { i18n } from "../../translate/i18n";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    //height: 400,
    [theme.breakpoints.down("sm")]: {
      maxHeight: "20vh",
    },
  },
  button: {
    marginRight: theme.spacing(1),
  },
  input: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
  addButton: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
}));

export function QueueOptionStepper({ queueId, options, updateOptions }) {
  const classes = useStyles();
  const [activeOption, setActiveOption] = useState(-1);
  const [attachment, setAttachment] = useState(null);
  const attachmentFile = useRef(null);
  const [transferModalOpen, setTransferModalOpen] = useState(false);
  const [selectedOptionForTransfer, setSelectedOptionForTransfer] = useState(null);
  const [queues, setQueues] = useState([]);


  // âœ… CARGAR DEPARTAMENTOS PARA TRANSFERENCIA
  const loadQueues = async () => {
    try {
      const { data } = await api.get("/queue");
      setQueues(data);
    } catch (e) {
      toastError(e);
    }
  };

  // âœ… ABRIR MODAL DE TRANSFERENCIA
  const openTransferModal = (option) => {
    setSelectedOptionForTransfer(option);
    setTransferModalOpen(true);
    loadQueues();
  };

  // âœ… ELIMINAR TRANSFERENCIA
  const handleRemoveTransfer = async (option) => {
    try {
      const optionToUpdate = { ...option, transferQueueId: null };

      await api.request({
        url: `/queue-options/${optionToUpdate.id}`,
        method: "PUT",
        data: optionToUpdate,
      });

      // âœ… ACTUALIZAR LA OPCIÃ“N EN EL ARRAY LOCAL
      const optionIndex = options.findIndex(opt => opt.id === optionToUpdate.id);
      if (optionIndex !== -1) {
        options[optionIndex] = optionToUpdate;
      }

      updateOptions();
      toastError("Transferencia eliminada correctamente");
    } catch (e) {
      toastError(e);
    }
  };

  // âœ… GUARDAR TRANSFERENCIA EN ESTADO TEMPORAL
  const handleSaveTransfer = async (transferQueueId) => {
    try {
      let optionToUpdate = { ...selectedOptionForTransfer, transferQueueId };

      // âœ… NO GUARDAR EN DB, SOLO MANTENER EN MEMORIA
      const optionIndex = options.findIndex(opt => opt === selectedOptionForTransfer);
      if (optionIndex !== -1) {
        options[optionIndex] = optionToUpdate;
      }

      setTransferModalOpen(false);
      setSelectedOptionForTransfer(null);
      updateOptions();

      // âœ… MOSTRAR MENSAJE TEMPORAL
      toastError("Transferencia configurada temporalmente. Presiona 'AGREGAR' para guardar el departamento completo.");
    } catch (e) {
      toastError(e);
    }
  };

  const handleOption = (index) => async () => {
    setActiveOption(index);
    const option = options[index];

    if (option !== undefined && option.id !== undefined) {
      try {
        const { data } = await api.request({
          url: "/queue-options",
          method: "GET",
          params: { queueId, parentId: option.id },
        });
        const optionList = data.map((option) => {
          return {
            ...option,
            children: [],
            edition: false,
          };
        });
        option.children = optionList;
        updateOptions();
      } catch (e) {
        toastError(e);
      }
    }
  };

  // âœ… GUARDAR OPCIÃ“N EN ESTADO TEMPORAL (NO EN DB)
  const handleSave = async (option) => {
    try {
      console.log("ðŸ’¾ Guardando opciÃ³n temporalmente:", option);

      // âœ… VERIFICAR QUE TENGA DATOS
      if (!option.title || option.title.trim() === "") {
        toastError("Debes ingresar un tÃ­tulo para la opciÃ³n");
        return;
      }

      // âœ… NO GUARDAR EN DB, SOLO MANTENER EN MEMORIA
      option.edition = false;

      // âœ… ASEGURAR QUE LOS DATOS SE MANTENGAN
      const optionIndex = options.findIndex(opt => opt === option);
      if (optionIndex !== -1) {
        options[optionIndex] = { ...option };
        console.log("âœ… OpciÃ³n guardada en memoria:", options[optionIndex]);
      }

      updateOptions();

      // âœ… MOSTRAR MENSAJE TEMPORAL
      toastError("OpciÃ³n guardada temporalmente. Presiona 'AGREGAR' para guardar el departamento completo.");
    } catch (e) {
      console.error("âŒ Error al guardar opciÃ³n:", e);
      toastError(e);
    }
  };



  const handleEdition = (index) => {
    options[index].edition = !options[index].edition;
    updateOptions();
  };

  const handleDeleteOption = async (index) => {
    const option = options[index];
    if (option !== undefined && option.id !== undefined) {
      try {
        await api.request({
          url: `/queue-options/${option.id}`,
          method: "DELETE",
        });
      } catch (e) {
        toastError(e);
      }
    }
    options.splice(index, 1);
    options.forEach(async (option, order) => {
      option.option = order + 1;
      await handleSave(option);
    });
    updateOptions();
  };

  const handleAttachmentFile = (e) => {
    const file = head(e.target.files);
    if (file) {
      setAttachment(file);
    }
  };

  const handleOptionChangeTitle = (event, index) => {
    options[index].title = event.target.value;
    updateOptions();
  };

  const handleOptionChangeMessage = (event, index) => {
    options[index].message = event.target.value;
    updateOptions();
  };

  const renderTitle = (index) => {
    const option = options[index];
    if (option.edition) {
      return (
        <>
          <TextField
            value={option.title}
            onChange={(event) => handleOptionChangeTitle(event, index)}
            size="small"
            className={classes.input}
            placeholder={i18n.t("queueOptions.placeholder.title")}
          />
                    <div style={{ display: "none" }}>
            <input
              type="file"
              ref={attachmentFile}
              onChange={(e) => handleAttachmentFile(e)}
            />
          </div>
          {option.edition && (
            <>
              <IconButton
                color="primary"
                variant="outlined"
                size="small"
                className={classes.button}
                onClick={() => handleSave(option)}
              >
                <SaveIcon />
              </IconButton>
              <IconButton
                variant="outlined"
                color="secondary"
                size="small"
                className={classes.button}
                onClick={() => handleDeleteOption(index)}
              >
                <DeleteOutlineIcon />
              </IconButton>
              {!attachment && !option.mediaPath && (
                <IconButton
                  variant="outlined"
                  color="primary"
                  size="small"
                  className={classes.button}
                    onClick={() => attachmentFile.current.click()}
                  >
                  <AttachFile/>
                </IconButton>
              )}
                             {(option.mediaPath || attachment) && (
                    <Grid xs={12} item>
                      <Button startIcon={<AttachFile />}>
                        {attachment != null
                          ? attachment.name
                          : option.mediaName}
                      </Button>

                        <IconButton
                          color="secondary"
                        >
                          <DeleteOutline />
                        </IconButton>
                    </Grid>
                  )}

              {/* âœ… ICONO DE TRANSFERENCIA DENTRO DEL MODO EDICIÃ“N - TEMPORALMENTE COMENTADO
              <IconButton
                variant="outlined"
                size="small"
                className={classes.button}
                onClick={() => openTransferModal(option)}
                style={{ 
                  color: option.transferQueueId ? 'green' : 'gray',
                  marginLeft: '5px'
                }}
                title="Configurar transferencia de departamento"
              >
                <LinkIcon />
              </IconButton>
              */}

              {/* âœ… INDICADOR DE TRANSFERENCIA CONFIGURADA */}
              {option.transferQueueId && (
                <>
                  {/* Buscar el nombre del departamento destino */}
                  {(() => {
                    const transferQueue = queues.find(q => q.id === option.transferQueueId);
                    return transferQueue ? (
                      <Typography
                        variant="body2"
                        style={{
                          display: 'inline-flex',
                          alignItems: 'center',
                          marginLeft: '8px',
                          color: 'green',
                          cursor: 'pointer',
                          fontSize: '0.875rem'
                        }}
                        onClick={() => openTransferModal(option)}
                        title="Click para editar transferencia"
                      >
                        â†’ {transferQueue.name}
                      </Typography>
                    ) : null;
                  })()}

                  {/* âœ… BOTÃ“N EDITAR TRANSFERENCIA */}
                  <IconButton
                    size="small"
                    className={classes.button}
                    onClick={() => openTransferModal(option)}
                    style={{ 
                      color: 'blue',
                      marginLeft: '4px'
                    }}
                    title="Editar transferencia"
                  >
                    <EditIcon />
                  </IconButton>

                  {/* âœ… BOTÃ“N ELIMINAR TRANSFERENCIA */}
                  <IconButton
                    size="small"
                    className={classes.button}
                    onClick={() => handleRemoveTransfer(option)}
                    style={{ 
                      color: 'red',
                      marginLeft: '2px'
                    }}
                    title="Eliminar transferencia"
                  >
                    <DeleteOutlineIcon />
                  </IconButton>
                </>
              )}
            </>
          )}
        </>
      );
    }
    return (
      <>
        <Typography>
          {option.title !== "" ? option.title : i18n.t("queueOptions.undefinedTitle")}
          <IconButton
            variant="outlined"
            size="small"
            className={classes.button}
            onClick={() => handleEdition(index)}
          >
            <EditIcon />
          </IconButton>
        </Typography>
      </>
    );
  };

  const renderMessage = (index) => {
    const option = options[index];
    if (option.edition) {
      return (
        <>
          <TextField
            style={{ width: "100%" }}
            multiline
            value={option.message}
            onChange={(event) => handleOptionChangeMessage(event, index)}
            size="small"
            className={classes.input}
            placeholder={i18n.t("queueOptions.placeholder.message")}
          />
        </>
      );
    }
    return (
      <>
        <Typography onClick={() => handleEdition(index)}>
          {option.message}
        </Typography>
      </>
    );
  };

  const handleAddOption = (index) => {
    const optionNumber = options[index].children.length + 1;
    options[index].children.push({
      title: "",
      message: "",
      edition: false,
      option: optionNumber,
      queueId,
      parentId: options[index].id,
      children: [],
    });
    updateOptions();
  };

  const renderStep = (option, index) => {
    return (
      <Step key={index}>
        <StepLabel style={{ cursor: "pointer" }} onClick={handleOption(index)}>
          {renderTitle(index)}
        </StepLabel>
        <StepContent>
          {renderMessage(index)}

          {option.id !== undefined && (
            <>
              <Button
                color="primary"
                size="small"
                onClick={() => handleAddOption(index)}
                startIcon={<AddIcon />}
                variant="outlined"
                className={classes.addButton}
              >
                {i18n.t("queueOptions.add")}
              </Button>
            </>
          )}
          <QueueOptionStepper
            queueId={queueId}
            options={option.children}
            updateOptions={updateOptions}
          />
        </StepContent>
      </Step>
    );
  };

  const renderStepper = () => {
    return (
      <Stepper
        style={{ marginBottom: 0, paddingBottom: 0 }}
        nonLinear
        activeStep={activeOption}
        orientation="vertical"
      >
        {options.map((option, index) => renderStep(option, index))}
      </Stepper>
    );
  };

  return (
    <>
      {renderStepper()}

      {/* âœ… MODAL DE TRANSFERENCIA */}
      <Dialog open={transferModalOpen} onClose={() => setTransferModalOpen(false)}>
        <DialogTitle>
          ðŸ”— Configurar Transferencia - {selectedOptionForTransfer?.title}
        </DialogTitle>
        <DialogContent>
          <Typography variant="body2" style={{ marginBottom: '16px' }}>
            Selecciona el departamento destino para esta opciÃ³n:
          </Typography>

          <FormControl fullWidth>
            <InputLabel>Departamento Destino</InputLabel>
            <Select
              value={selectedOptionForTransfer?.transferQueueId || ""}
              onChange={(e) => {
                const updatedOption = { ...selectedOptionForTransfer, transferQueueId: e.target.value };
                setSelectedOptionForTransfer(updatedOption);
              }}
            >
              <MenuItem value="">
                <em>-- Sin transferencia --</em>
              </MenuItem>
              {queues.map((queue) => (
                <MenuItem key={queue.id} value={queue.id}>
                  {queue.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setTransferModalOpen(false)} color="secondary">
            Cancelar
          </Button>
          <Button 
            onClick={() => handleSaveTransfer(selectedOptionForTransfer?.transferQueueId)}
            color="primary"
            variant="contained"
          >
            Guardar Transferencia
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
}

export function QueueOptions({ queueId, onSaveOptionsRef }) {
  const classes = useStyles();
  const [options, setOptions] = useState([]);

  useEffect(() => {
    if (queueId) {
      const fetchOptions = async () => {
        try {
          const { data } = await api.request({
            url: "/queue-options",
            method: "GET",
            params: { queueId, parentId: -1 },
          });
          const optionList = data.map((option) => {
            return {
              ...option,
              children: [],
              edition: false,
            };
          });
          setOptions(optionList);
        } catch (e) {
          toastError(e);
        }
      };
      fetchOptions();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const renderStepper = () => {
    if (options.length > 0) {
      return (
        <QueueOptionStepper
          queueId={queueId}
          updateOptions={updateOptions}
          options={options}
          saveAllOptions={saveAllOptions}
        />
      );
    }
  };

  // âœ… GUARDAR TODAS LAS OPCIONES EN DB
  const saveAllOptions = useCallback(async () => {
    try {
      console.log("ðŸ”„ saveAllOptions iniciado con opciones:", options);
      console.log("ðŸ” Detalles de opciones:", JSON.stringify(options, null, 2));
      console.log("ðŸ” NÃºmero de opciones:", options.length);
      console.log("ðŸ” Opciones con tÃ­tulo:", options.filter(opt => opt.title && opt.title.trim() !== "").length);

      // âœ… VERIFICAR SI HAY OPCIONES PARA GUARDAR
      if (!options || options.length === 0) {
        console.log("âš ï¸ No hay opciones para guardar");
        return;
      }

      let savedCount = 0;

      // âœ… GUARDAR CADA OPCIÃ“N EN DB
      for (const option of options) {
        console.log(`ðŸ“ Procesando opciÃ³n:`, option);
        console.log(`ðŸ” TÃ­tulo: "${option.title}", ID: ${option.id}, QueueId: ${option.queueId}`);

        if (option.title && option.title.trim() !== "") {
          if (option.id) {
            // âœ… UPDATE OPCIÃ“N EXISTENTE
            console.log(`ðŸ”„ UPDATE opciÃ³n existente ID: ${option.id}`);
            const updateResponse = await api.request({
              url: `/queue-options/${option.id}`,
              method: "PUT",
              data: option,
            });
            console.log(`âœ… UPDATE respuesta:`, updateResponse);
            savedCount++;
          } else {
            // âœ… INSERT NUEVA OPCIÃ“N
            console.log(`âž• INSERT nueva opciÃ³n: ${option.title}`);
            console.log(`ðŸ“¤ Datos a enviar:`, option);
            const { data } = await api.request({
              url: `/queue-options`,
              method: "POST",
              data: option,
            });
            console.log(`âœ… INSERT respuesta:`, data);
            option.id = data.id;
            console.log(`âœ… Nueva opciÃ³n creada con ID: ${data.id}`);
            savedCount++;
          }

          // âœ… GUARDAR ARCHIVOS ADJUNTOS
          if (option.mediaPath) {
            console.log(`ðŸ“Ž Guardando archivo adjunto para opciÃ³n ${option.id}`);
            const formData = new FormData();
            formData.append("file", option.mediaPath);
            await api.post(`/queue-options/${option.id}/media-upload`, formData);
          }
        } else {
          console.log(`âš ï¸ OpciÃ³n sin tÃ­tulo, saltando:`, option);
        }
      }

      if (savedCount > 0) {
        console.log(`âœ… saveAllOptions completado exitosamente. ${savedCount} opciones guardadas.`);
        toastError(`Todas las opciones guardadas correctamente.`);
      } else {
        console.log("âš ï¸ No se guardÃ³ ninguna opciÃ³n");
      }
    } catch (e) {
      console.error("âŒ Error en saveAllOptions:", e);
      console.error("âŒ Stack trace:", e.stack);
      toastError(e);
    }
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const updateOptions = () => {
    setOptions([...options]);
  };

  // âœ… EXPONER FUNCIÃ“N A TRAVÃ‰S DE REFERENCIA
  useEffect(() => {
    if (onSaveOptionsRef) {
      console.log("ðŸ”— Pasando saveAllOptions al padre");
      console.log("ðŸ” Tipo de saveAllOptions:", typeof saveAllOptions);
      console.log("ðŸ” Opciones actuales:", options.length);
      onSaveOptionsRef(saveAllOptions);
    }
  }, [onSaveOptionsRef]); // eslint-disable-line react-hooks/exhaustive-deps

  const addOption = () => {
    const newOption = {
      title: "",
      message: "",
      edition: false,
      option: options.length + 1,
      queueId,
      parentId: null,
      children: [],
    };
    setOptions([...options, newOption]);
  };

  return (
    <div className={classes.root}>
      <br />
      <Typography>
        {i18n.t("queueOptions.title")}
        <Button
          color="primary"
          size="small"
          onClick={addOption}
          startIcon={<AddIcon />}
          style={{ marginLeft: 10 }}
          variant="outlined"
        >
          {i18n.t("queueOptions.add")}
        </Button>
      </Typography>
      {renderStepper()}
    </div>
  );
}
