          <div className={classes.replyginMsgBody}>
            {!message.fromMe && (
              <span className={classes.messageContactName}>
                {message.contact?.name}
              </span>
            )}
            {message.body}
          </div>
        </div>
        <IconButton
          aria-label="showRecorder"
          component="span"
          disabled={loading || ticketStatus !== "open"}
          onClick={() => setReplyingMessage(null)}
        >
          <ClearIcon className={classes.sendMessageIcons} />
        </IconButton>
      </div>
    );
  };

  if (medias.length > 0)
    return (
      <Paper elevation={0} square className={classes.viewMediaInputWrapper}>
        <IconButton
          aria-label="cancel-upload"
          component="span"
          onClick={(e) => setMedias([])}
        >
          <CancelIcon className={classes.sendMessageIcons} />
        </IconButton>

        {loading ? (
          <div>
            {/*<CircularProgress className={classes.circleLoading} />*/}
            <LinearWithValueLabel progress={percentLoading} />
          </div>
        ) : (
          <span>
            {medias[0]?.name}
            {/* <img src={media.preview} alt=""></img> */}
          </span>
        )}
        <IconButton
          aria-label="send-upload"
          component="span"
          onClick={handleUploadMedia}
          disabled={loading}
        >
          <SendIcon className={classes.sendMessageIcons} />
        </IconButton>
      </Paper>
    );
  else {
    return (
      <Paper square elevation={0} className={classes.mainWrapper}>
        {replyingMessage && renderReplyingMessage(replyingMessage)}
        <div className={classes.newMessageBox}>
          <EmojiOptions
            disabled={disableOption()}
            handleAddEmoji={handleAddEmoji}
            showEmoji={showEmoji}
            setShowEmoji={setShowEmoji}
          />

          <FileInput
            disableOption={disableOption}
            handleChangeMedias={handleChangeMedias}
          />

          <IconButton
            aria-label="quickReplies"
            component="span"
            disabled={disableOption()}
            onClick={handleQuickReplyButtonClick}
            title="Respuestas RÃ¡pidas"
          >
            <FlashOnIcon className={classes.sendMessageIcons} />
          </IconButton>

          <IconButton
            aria-label="fileLists"
            component="span"
            disabled={disableOption()}
            onClick={handleFileListButtonClick}
            title="Listas de Archivos"
          >
            <FolderIcon className={classes.sendMessageIcons} />
          </IconButton>

          <Menu
            anchorEl={fileListAnchorEl}
            open={Boolean(fileListAnchorEl)}
            onClose={handleFileListMenuClose}
            PaperProps={{
              style: {
                maxHeight: 300,
                width: 300,
              },
            }}
          >
            {fileListOptions.map((fileList) => (
              <MenuItem
                key={fileList.value.id}
                onClick={() => handleFileListSelect(fileList)}
                style={{ 
                  display: 'flex', 
                  alignItems: 'center',
                  fontSize: '14px',
                  padding: '8px 16px'
                }}
              >
                {fileList.label}
              </MenuItem>
            ))}
            {fileListOptions.length === 0 && (
              <MenuItem disabled>
                No hay listas de archivos disponibles
              </MenuItem>
            )}
          </Menu>

          <SignSwitch
            width={props.width}
            setSignMessage={setSignMessage}
            signMessage={signMessage}
          />

          <CustomInput
            loading={loading}
            inputRef={inputRef}
            ticketStatus={ticketStatus}
            inputMessage={inputMessage}
            setInputMessage={setInputMessage}
            // handleChangeInput={handleChangeInput}
            handleSendMessage={handleSendMessage}
            handleInputPaste={handleInputPaste}
            disableOption={disableOption}
            replyingMessage={replyingMessage}
            handleQuickAnswersClick={handleQuickAnswersClick}
            options={options}
            popupOpen={popupOpen}
          />

          <ActionButtons
            inputMessage={inputMessage}
            loading={loading}
            recording={recording}
            ticketStatus={ticketStatus}
            handleSendMessage={handleSendMessage}
            handleCancelAudio={handleCancelAudio}
            handleUploadAudio={handleUploadAudio}
            handleStartRecording={handleStartRecording}
            handleOpenModalForward={handleOpenModalForward}
            showSelectMessageCheckbox={showSelectMessageCheckbox}
            setShowSelectMessageCheckbox={setShowSelectMessageCheckbox}
            setSelectedMessages={setSelectedMessages}
          />
        </div>
      </Paper>
    );
  }
};

export default withWidth()(MessageInputCustom);
