        alpha.indexOf('%') > -1 ? parseFloat(alpha.substring(0, alpha.length - 1), 10) / 100 : parseFloat(alpha, 10);
    }
    alpha = Math.floor(alpha * 255);
    rgb[3] = alpha;
  }
  const value = rgbToInt(rgb);
